			WHERE IdFormaPago = p_IdFormaPago;
            
	END IF;

	IF p_Opc = 'D'
	THEN
		DELETE
			FROM forma_pago
		WHERE IdFormaPago = p_IdFormaPago;
	END IF;
    
    
	IF p_Opc = 'S'
	THEN
		SELECT IdFormaPago, FormaPago
			FROM forma_pago
		WHERE IdFormaPago = p_IdFormaPago;
	END IF;


	IF p_Opc = 'X'
	THEN
		SELECT IdFormaPago, FormaPago
			FROM forma_pago;
		
	END IF;
    
END ;;
